from annotator import *

ERROR_LIST = []
SUCCESS_LIST = []

class SVAnnotatorTester(object):
    def __init__(self,gene1,gene2,chr1,chr2,pos1,pos2,desc1,desc2,svType,event,logger,annotation,comment,position):
        __annot = SVAnnotator(gene1,gene2,chr1,chr2,pos1,pos2,desc1,desc2,svType,event,logger).get_notes()
        __key = "{}|{}|{}|{}|{}|{}|{}|{}|{}|{}".format(gene1,gene2,chr1,chr2,pos1,pos2,desc1,desc2,svType,event)
        __type = 'N/A'
        if "vIII" in comment : __type = "vIII"
        elif "CTD" in comment : __type = "CTD"
        elif "KDD" in comment : __type = "KDD"
        elif "fusion" in annotation : __type = "FUSION"
        if __annot[0] != annotation or __annot[1].replace("Note : The","The") != comment or __annot[2] != position or __annot[3] != __type:
            ERROR_LIST.append(__key)
            # print "annotation :",annotation
            # print "__annot[0] :",__annot[0]
            # print "comment :",comment
            # print "__annot[1] :",__annot[1]
            # print "position :",position
            # print "__annot[2] :",__annot[2]
        else:
            SUCCESS_LIST.append(__key)

